import React from "react";

import "../../styles/response.scss";
import "../../styles/response-item.scss"
import ""

function ResponseItem({ img, text, username }) {
  return (
    <div className="response-item">
      <p className="response-item__text">{text}</p>
      <img className="response-item__img" src={img} alt="User avatar" />
      <p className="response-item__username">{username}</p>
    </div>
  );
}

export default function Response() {
  return (
    <div className="response">
      <div className="response__content">
        <div className="response__title">Відгуки</div>
        <div className="response__items">
          <ResponseItem
            img={"../"}
            text={"Дуже чудовий сервіс.\nШвидко відповідають на повідомлення та вчасно виконують замовлення відповідно до усіх побажань))"}
            username={"Khrysty"}
          />
          <ResponseItem
            img={"url_to_your_image"}
            text={"Неймовірне оформлення та дуже швидка доставка!\nБули враховані всі побажання, які були обговорені при замовленні."}
            username={"Andrii Bereznyuk"}
          />
          <ResponseItem
            img={"url_to_your_image"}
            text={"г, вона просто в захваті!"}
            username={"Denis Khlibov"}
          />
        </div>
      </div>
      <div className="response__line"></div>
    </div>
  );
}
