const defaultState = {
  lang: localStorage.getItem("lang") || "uk",
};

const reducer = (state = defaultState, action) => {
  switch (action.type) {
    case "CHANGE_LANG":
      localStorage.setItem("lang", action.payload);
      return { ...state, lang: action.payload };
    default:
      return state;
  }
};

export default reducer;
