const defaultState = {
    lang: "uk",
  };

  const reducer = (state = defaultState, action) => {
    switch (action.type) {
      case "CHANGE_LANG":
        return { ...state, lang: action.payload };
      default:
        return state;
    }
  };

  export default reducer;
